public class assignment_2_8java {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		myMethod();

	}

	private static void myMethod() {
		// TODO Auto-generated method stub
		int n = 7;
		int result = 1;
		for (int i = 1; i <= n; i++) {
			result = result * 1;
			
		}
		System.out.println("The factorial of 7 is " + result);
	}

}
