package com.upskill.java_1;

public class Accessmodifier {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "fahim";            // public - Any class can access
		
		
				
	    int age = 30;                    // private - Only same class can access
	    
	    
	    int ssn = 424534;              // protected - classes in the same package and subclass
	    
	           String address = "Heritage Tower"; // default - classes in same package can access

	}

}
